%%  Increasing Cumulative Gaussian between lapse and theta%  parameters:%    p(1) = mean%    p(2) = std. dev.%	 p(3) = theta - should be close to 1%    p(4) = lapse - should be close to 0%  23-07-2004  Pascal Mamassian% Adapted by Adrien chopinfunction y = cumulGauss6(x,p)if numel(p)<4;     if numel(p)<3; p(3)=1; end    p(4)=0; endy = p(4)+(p(3)-p(4)).*(0.5 + erf(((x - p(1)) ./ p(2)) ./ sqrt(2)) ./ 2);end